import React, {useState} from 'react';
import Expense from './component/Expense.js';
import NewExpense from './component/NewExpense/NewExpense.js';

const DUMMY_EXPENSE = [
  {
    id: 'e1',
    title: 'A4 Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  {
    id: 'e2',
    title: 'New TV',
    amount: 799.49,
    date: new Date(2021, 2, 12),
  },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {
  const [expenses, setExpense] = useState(DUMMY_EXPENSE);
  const [expenseFilter, setExpenseFilter] = useState(expenses.slice());

  const addExpense = (expenseData) => {
    setExpense((prevExpense) => {
      return [expenseData, ...prevExpense];
    });
    console.log(expenses);
  };

  const yearChange = (year) => {
    const x = expenseFilter.filter(el => String(el.date.getFullYear()) === year);
    setExpenseFilter(x);
  };

  return (
      <div>
        <NewExpense onAddExpense={addExpense}/>
        <Expense items={expenseFilter} onYearChange={yearChange}/>
      </div>
  );
}

export default App;
